{% assign person = Workflow | Attribute:'Person','Object' %}
{% sql %}
    SELECT w.Guid, av.[Value]
      FROM Workflow w
        JOIN AttributeValue av ON (w.Id = av.EntityId)
     WHERE w.WorkflowTypeId = {{ Workflow | Attribute:'BaptismClassWorkflowType','Object' | Property:'Id' }}
       AND w.Status = 'Active'
       AND av.AttributeId = {{ Workflow | Attribute:'BaptismClassPersonCompletingCourseAttributeId' }}
       AND av.Value IN (SELECT pa.[Guid]
                          FROM PersonAlias pa
                        	JOIN Person p ON (pa.PersonId = p.Id)
                         WHERE pa.PersonId = '{{ person.Id }}')
{% endsql %}

